mapper.update:
UPDATE review
SET rating = #{rating}, content = #{content}
WHERE review_id = #{reviewId}

mapper.select:
SELECT * FROM review
WHERE product_id = #{productId}
ORDER BY created_at DESC

SELECT * FROM review
WHERE product_id = #{productId}
ORDER BY liked DESC

mapper.update:
UPDATE review
SET liked = liked + 1
WHERE review_id = #{reviewId}

UPDATE review
SET liked = liked - 1
WHERE review_id = #{reviewId}

UPDATE product
SET rating_sum = rating_sum + ${rating}, rating_cnt = rating_cnt + 1
WHERE product_id = #{productId}

UPDATE product
SET rating_sum = rating_sum - ${rating}, rating_cnt = rating_cnt - 1
WHERE product_id = #{productId}
