mapper.update:
UPDATE review
SET rating = #{rating}, review_image = #{reviewImage}, content = #{content}
WHERE review_id = #{reviewId}

UPDATE review
SET liked = liked + 1
WHERE review_id = #{reviewId}

UPDATE review
SET liked = liked - 1
WHERE review_id = #{reviewId}

UPDATE product
SET rating_sum = rating_sum + ${rating}, rating_cnt = rating_cnt + 1
WHRER product_id = #{productId}

UPDATE product
SET rating_sum = rating_sum - ${rating}, rating_cnt = rating_cnt - 1
WHRER product_id = #{productId}
